import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import config from '../config';
import './RegionsPage.css';

const RegionsPage = () => {
  const [regions, setRegions] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [userRole, setUserRole] = useState('');
  const [newRegion, setNewRegion] = useState({ name: '', description: '' });
  const [editingRegion, setEditingRegion] = useState(null);
  const [showForm, setShowForm] = useState(false);
  const [successMessage, setSuccessMessage] = useState('');

  const navigate = useNavigate();

  // Check if user is admin
  const isAdmin = userRole === 'admin';

  useEffect(() => {
    // Fetch regions data
    fetchRegions();
    
    // Get user role from token
    const token = localStorage.getItem('token');
    if (token) {
      try {
        const base64Url = token.split('.')[1];
        const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
        const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        }).join(''));
        
        const decodedToken = JSON.parse(jsonPayload);
        setUserRole(decodedToken.role);
      } catch (error) {
        console.error('Error decoding token:', error);
      }
    } else {
      navigate('/auth');
    }
  }, [navigate]);

  const fetchRegions = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return;
      }

      const response = await fetch(`${config.apiUrl}/api/regions`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error(`Error ${response.status}: ${response.statusText}`);
      }

      const data = await response.json();
      setRegions(data.regions || []);
    } catch (error) {
      console.error('Error fetching regions:', error);
      setError('Failed to load regions. Please try again later.');
    } finally {
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    if (editingRegion) {
      setEditingRegion(prev => ({ ...prev, [name]: value }));
    } else {
      setNewRegion(prev => ({ ...prev, [name]: value }));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!isAdmin) return;
    
    setSuccessMessage('');
    setError(null);
    
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return;
      }
      
      let url = `${config.apiUrl}/api/regions`;
      let method = 'POST';
      let body = newRegion;
      
      // If editing, use PUT method and include region ID
      if (editingRegion) {
        url = `${url}/${editingRegion.id}`;
        method = 'PUT';
        body = editingRegion;
      }
      
      const response = await fetch(url, {
        method,
        headers: {
          'Content-Type': 'application/json',
          'Authorization': `Bearer ${token}`
        },
        body: JSON.stringify(body)
      });
      
      if (!response.ok) {
        throw new Error(`Error ${response.status}: ${response.statusText}`);
      }
      
      // Refresh regions list
      fetchRegions();
      
      // Reset form
      setNewRegion({ name: '', description: '' });
      setEditingRegion(null);
      setShowForm(false);
      
      // Show success message
      setSuccessMessage(editingRegion ? 'Region updated successfully!' : 'Region added successfully!');
    } catch (error) {
      console.error('Error saving region:', error);
      setError('Failed to save region. Please try again.');
    }
  };

  const handleEdit = (region) => {
    if (!isAdmin) return;
    setEditingRegion(region);
    setShowForm(true);
    setSuccessMessage('');
  };

  const handleDelete = async (id) => {
    if (!isAdmin) return;
    
    if (!window.confirm('Are you sure you want to delete this region?')) {
      return;
    }
    
    setSuccessMessage('');
    setError(null);
    
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return;
      }
      
      const response = await fetch(`${config.apiUrl}/api/regions/${id}`, {
        method: 'DELETE',
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });
      
      if (!response.ok) {
        throw new Error(`Error ${response.status}: ${response.statusText}`);
      }
      
      // Refresh regions list
      fetchRegions();
      setSuccessMessage('Region deleted successfully!');
    } catch (error) {
      console.error('Error deleting region:', error);
      setError('Failed to delete region. Please try again.');
    }
  };

  const handleCancel = () => {
    setShowForm(false);
    setEditingRegion(null);
    setNewRegion({ name: '', description: '' });
    setSuccessMessage('');
  };

  const handleBack = () => {
    navigate('/dashboard');
  };

  return (
    <div className="regions-page">
      <div className="regions-header">
        <button className="back-button" onClick={handleBack}>Back to Dashboard</button>
        <h1>Regions in Spain</h1>
        {isAdmin && !showForm && (
          <button 
            className="add-button" 
            onClick={() => setShowForm(true)}
          >
            Add New Region
          </button>
        )}
      </div>

      {successMessage && <div className="success-message">{successMessage}</div>}
      {error && <div className="error-message">{error}</div>}

      {showForm && isAdmin && (
        <div className="region-form-container">
          <form className="region-form" onSubmit={handleSubmit}>
            <h2>{editingRegion ? 'Edit Region' : 'Add New Region'}</h2>
            <div className="form-group">
              <label htmlFor="name">Region Name *</label>
              <input
                type="text"
                id="name"
                name="name"
                value={editingRegion ? editingRegion.name : newRegion.name}
                onChange={handleInputChange}
                placeholder="E.g., Andalusia, Catalonia"
                required
              />
            </div>
            <div className="form-group">
              <label htmlFor="description">Description *</label>
              <textarea
                id="description"
                name="description"
                value={editingRegion ? editingRegion.description : newRegion.description}
                onChange={handleInputChange}
                placeholder="Describe the region..."
                rows={4}
                required
              />
            </div>
            <div className="form-actions">
              <button type="submit" className="save-button">
                {editingRegion ? 'Update Region' : 'Add Region'}
              </button>
              <button type="button" className="cancel-button" onClick={handleCancel}>
                Cancel
              </button>
            </div>
          </form>
        </div>
      )}

      <div className="regions-list">
        {loading ? (
          <p>Loading regions...</p>
        ) : regions.length === 0 ? (
          <p>No regions found.</p>
        ) : (
          regions.map((region) => (
            <div key={region.id} className="region-card">
              <div className="region-content">
                <h2>{region.name}</h2>
                <p>{region.description}</p>
              </div>
              
              {isAdmin && (
                <div className="region-actions">
                  <button 
                    className="edit-button" 
                    onClick={() => handleEdit(region)}
                  >
                    Edit
                  </button>
                  <button 
                    className="delete-button" 
                    onClick={() => handleDelete(region.id)}
                  >
                    Delete
                  </button>
                </div>
              )}
            </div>
          ))
        )}
      </div>
    </div>
  );
};

export default RegionsPage;
