import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import config from '../config';
import './AuthPage.css';

const AuthPage = () => {
  const [activeTab, setActiveTab] = useState('login');

  const [registerData, setRegisterData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    password: '',
    accountType: 'client',
  });

  const [loginData, setLoginData] = useState({
    email: '',
    password: '',
  });

  const [errorMessage, setErrorMessage] = useState('');
  const [successMessage, setSuccessMessage] = useState('');
  const [loading, setLoading] = useState(false);

  const navigate = useNavigate();

  const handleRegisterChange = (e) => {
    const { name, value } = e.target;
    setRegisterData((prev) => ({ ...prev, [name]: value }));
  };

  const handleLoginChange = (e) => {
    const { name, value } = e.target;
    setLoginData((prev) => ({ ...prev, [name]: value }));
  };

  const handleTabClick = (tab) => {
    setActiveTab(tab);
    setErrorMessage('');
    setSuccessMessage('');
  };

  const handleRegisterSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setSuccessMessage('');
    setLoading(true);
    try {
      console.log('Sending registration data:', registerData);
      const response = await fetch(`${config.apiUrl}/api/users/register`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(registerData)
      });
      const data = await response.json();
      if (response.ok) {
        setSuccessMessage('Registration successful! Please check your email to verify your account.');
        // Reset the form
        setRegisterData({
          firstName: '',
          lastName: '',
          email: '',
          password: '',
          accountType: 'client',
        });
      } else {
        setErrorMessage(data.error || 'Registration failed');
      }
    } catch (error) {
      setErrorMessage('An error occurred during registration');
    } finally {
      setLoading(false);
    }
  };

  const handleLoginSubmit = async (e) => {
    e.preventDefault();
    setErrorMessage('');
    setLoading(true);
    try {
      const response = await fetch(`${config.apiUrl}/api/users/login`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify(loginData)
      });
      const data = await response.json();
      if (response.ok) {
        // Store auth token in localStorage
        if (data.token) {
          localStorage.setItem('token', data.token);
          // Redirect to dashboard
          navigate('/dashboard');
        } else {
          setErrorMessage('Authentication token missing in response from server.');
        }
      } else {
        if (data.needsVerification) {
          // User registered but email not verified
          navigate(`/verify-email?id=${data.userId}`);
        } else if (data.awaitingApproval) {
          // User is verified but waiting for admin approval
          setErrorMessage('Your account is verified but waiting for admin approval. Please try again later or contact support.');
        } else {
          setErrorMessage(data.error || 'Login failed');
        }
      }
    } catch (error) {
      setErrorMessage('An error occurred during login');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="auth-page">
      <div className="auth-container">
        <div className="auth-form-container">
          <h2>Spain Undiscovered</h2>
          <p className="subtitle">Sign in to access your account</p>
          <div className="tabs">
            <button
              className={activeTab === 'login' ? 'active' : ''}
              onClick={() => handleTabClick('login')}
            >
              Login
            </button>
            <button
              className={activeTab === 'register' ? 'active' : ''}
              onClick={() => handleTabClick('register')}
            >
              Register
            </button>
          </div>
          {activeTab === 'login' && (
            <form onSubmit={handleLoginSubmit} className="form">
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={loginData.email}
                onChange={handleLoginChange}
                placeholder="your.email@example.com"
                required
              />
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={loginData.password}
                onChange={handleLoginChange}
                placeholder="********"
                required
              />
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              <button type="submit" className="submit-button" disabled={loading}>
                {loading ? 'Logging in...' : 'Login'}
              </button>
            </form>
          )}
          {activeTab === 'register' && (
            <form onSubmit={handleRegisterSubmit} className="form">
              <div className="name-fields">
                <div>
                  <label>First Name</label>
                  <input
                    type="text"
                    name="firstName"
                    value={registerData.firstName}
                    onChange={handleRegisterChange}
                    placeholder="John"
                    required
                  />
                </div>
                <div>
                  <label>Last Name</label>
                  <input
                    type="text"
                    name="lastName"
                    value={registerData.lastName}
                    onChange={handleRegisterChange}
                    placeholder="Doe"
                    required
                  />
                </div>
              </div>
              <label>Email</label>
              <input
                type="email"
                name="email"
                value={registerData.email}
                onChange={handleRegisterChange}
                placeholder="your.email@example.com"
                required
              />
              <label>Password</label>
              <input
                type="password"
                name="password"
                value={registerData.password}
                onChange={handleRegisterChange}
                placeholder="********"
                required
              />
              <label>Account Type</label>
              <select
                name="accountType"
                value={registerData.accountType}
                onChange={handleRegisterChange}
              >
                <option value="client">Client</option>
                <option value="agency">Agency</option>
              </select>
              {errorMessage && <p className="error-message">{errorMessage}</p>}
              {successMessage && <p className="success-message">{successMessage}</p>}
              <button type="submit" className="submit-button" disabled={loading}>
                {loading ? 'Registering...' : 'Register'}
              </button>
            </form>
          )}
        </div>
        <div className="auth-info">
          <h2>Discover Spanish Real Estate</h2>
          <p>
            Join our platform to explore exclusive properties in Spain's most beautiful regions.
            Whether you're a client looking for your dream home or an agency showcasing your listings,
            Spain Undiscovered provides all the tools you need.
          </p>
          <ul>
            <li>Browse properties across Spain's top regions</li>
            <li>Connect with reputable agencies</li>
            <li>Access exclusive travel guides</li>
            <li>Stay updated with the latest market trends</li>
          </ul>
        </div>
      </div>
    </div>
  );
};

export default AuthPage;
