import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import config from '../config';
import './ProfilePage.css';

const ProfilePage = () => {
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [successMessage, setSuccessMessage] = useState('');
  const [userRole, setUserRole] = useState('');
  const [profileImage, setProfileImage] = useState(null);
  const [imagePreview, setImagePreview] = useState(null);
  
  const [formData, setFormData] = useState({
    firstName: '',
    lastName: '',
    email: '',
    address: '',
    zipCode: '',
    phoneNumber: '',
    whatsapp: '',
    teams: '',
    telegram: '',
    discord: '',
    bio: ''
  });
  
  const navigate = useNavigate();

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    setLoading(true);
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return;
      }

      // Get user role from token
      try {
        const base64Url = token.split('.')[1];
        const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
        const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
          return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
        }).join(''));
        
        const decodedToken = JSON.parse(jsonPayload);
        setUserRole(decodedToken.role);
      } catch (e) {
        console.error('Error decoding token:', e);
      }

      // Fetch profile data
      const response = await fetch(`${config.apiUrl}/api/users/profile`, {
        headers: {
          'Authorization': `Bearer ${token}`
        }
      });

      if (!response.ok) {
        throw new Error('Failed to fetch user profile');
      }

      const profileData = await response.json();
      
      // Set form data from profile
      setFormData({
        firstName: profileData.first_name || '',
        lastName: profileData.last_name || '',
        email: profileData.email || '',
        address: profileData.address || '',
        zipCode: profileData.zip_code || '',
        phoneNumber: profileData.phone_number || '',
        whatsapp: profileData.whatsapp || '',
        teams: profileData.teams || '',
        telegram: profileData.telegram || '',
        discord: profileData.discord || '',
        bio: profileData.bio || ''
      });

      // Set profile image if exists
      if (profileData.profile_image) {
        setImagePreview(`${config.apiUrl}/uploads/${profileData.profile_image}`);
      }
      
      setLoading(false);
    } catch (err) {
      console.error('Error fetching profile:', err);
      setError('Failed to load profile data. Please try again later.');
      setLoading(false);
    }
  };

  const handleInputChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setProfileImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result);
      };
      reader.readAsDataURL(file);
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setSuccessMessage('');
    setError(null);
    
    try {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return;
      }

      const formDataToSend = new FormData();
      
      // Append text fields
      Object.keys(formData).forEach(key => {
        formDataToSend.append(key, formData[key]);
      });
      
      // Append image if selected
      if (profileImage) {
        formDataToSend.append('profileImage', profileImage);
      }

      const response = await fetch(`${config.apiUrl}/api/users/profile`, {
        method: 'PUT',
        headers: {
          'Authorization': `Bearer ${token}`
        },
        body: formDataToSend
      });

      if (!response.ok) {
        const data = await response.json();
        throw new Error(data.error || 'Failed to update profile');
      }

      setSuccessMessage('Profile updated successfully');
    } catch (err) {
      console.error('Error updating profile:', err);
      setError(err.message || 'Failed to update profile. Please try again.');
    }
  };

  const handleBack = () => {
    navigate('/dashboard');
  };

  const renderRequiredLabel = () => (
    <span className="required-label">*</span>
  );

  if (loading) {
    return <div className="profile-loading">Loading profile data...</div>;
  }

  return (
    <div className="profile-page">
      <header className="profile-header">
        <button className="back-button" onClick={handleBack}>Back to Dashboard</button>
        <h1>My Profile</h1>
        <p>Update your personal information</p>
      </header>

      {successMessage && <div className="success-message">{successMessage}</div>}
      {error && <div className="error-message">{error}</div>}

      <form className="profile-form" onSubmit={handleSubmit}>
        <div className="profile-sections">
          <div className="profile-section">
            <h2>Personal Information</h2>
            
            <div className="profile-image-container">
              <div className="profile-image">
                {imagePreview ? (
                  <img src={imagePreview} alt="Profile Preview" />
                ) : (
                  <div className="profile-placeholder">
                    {formData.firstName ? formData.firstName.charAt(0).toUpperCase() : 'U'}
                  </div>
                )}
              </div>
              <div className="profile-image-upload">
                <label htmlFor="profile-image-input" className="upload-button">
                  Upload Photo
                </label>
                <input 
                  id="profile-image-input" 
                  type="file" 
                  accept="image/*"
                  onChange={handleImageChange}
                />
                <p className="image-hint">JPG or PNG, max 2MB</p>
              </div>
            </div>
            
            <div className="form-row">
              <div className="form-group">
                <label>First Name {renderRequiredLabel()}</label>
                <input 
                  type="text" 
                  name="firstName" 
                  value={formData.firstName} 
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Last Name {renderRequiredLabel()}</label>
                <input 
                  type="text" 
                  name="lastName" 
                  value={formData.lastName} 
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
            
            <div className="form-group">
              <label>Email Address {renderRequiredLabel()}</label>
              <input 
                type="email" 
                name="email" 
                value={formData.email} 
                onChange={handleInputChange}
                required
                readOnly
              />
            </div>

            <div className="form-group">
              <label>Address {renderRequiredLabel()}</label>
              <input 
                type="text" 
                name="address" 
                value={formData.address} 
                onChange={handleInputChange}
                required
              />
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>ZIP Code {renderRequiredLabel()}</label>
                <input 
                  type="text" 
                  name="zipCode" 
                  value={formData.zipCode} 
                  onChange={handleInputChange}
                  required
                />
              </div>
              <div className="form-group">
                <label>Phone Number {renderRequiredLabel()}</label>
                <input 
                  type="tel" 
                  name="phoneNumber" 
                  value={formData.phoneNumber} 
                  onChange={handleInputChange}
                  required
                />
              </div>
            </div>
          </div>
          
          <div className="profile-section">
            <h2>Additional Contact Information</h2>
            <p className="section-description">Optional contact methods</p>

            <div className="form-row">
              <div className="form-group">
                <label>WhatsApp</label>
                <input 
                  type="text" 
                  name="whatsapp" 
                  value={formData.whatsapp} 
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Microsoft Teams</label>
                <input 
                  type="text" 
                  name="teams" 
                  value={formData.teams} 
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-row">
              <div className="form-group">
                <label>Telegram</label>
                <input 
                  type="text" 
                  name="telegram" 
                  value={formData.telegram} 
                  onChange={handleInputChange}
                />
              </div>
              <div className="form-group">
                <label>Discord</label>
                <input 
                  type="text" 
                  name="discord" 
                  value={formData.discord} 
                  onChange={handleInputChange}
                />
              </div>
            </div>

            <div className="form-group">
              <label>Bio</label>
              <textarea 
                name="bio" 
                value={formData.bio} 
                onChange={handleInputChange}
                rows={5}
                placeholder="Tell us a bit about yourself..."
              />
            </div>

            <div className="form-actions">
              <button type="submit" className="save-button">Save Changes</button>
              <button type="button" className="cancel-button" onClick={handleBack}>Cancel</button>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default ProfilePage;
