import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import config from '../config';
import './Dashboard.css';

const Dashboard = () => {
  const [userRole, setUserRole] = useState(null);
  const [userName, setUserName] = useState('');
  const [userAvatar, setUserAvatar] = useState(null);
  const [stats, setStats] = useState({
    properties: 0,
    regions: 0,
    agencies: 0,
    leads: 0,
  });
  const [recentLeads, setRecentLeads] = useState([]);
  const [displayName, setDisplayName] = useState('');
  const navigate = useNavigate();

  // Function to refresh token
  const refreshToken = async () => {
    try {
      // Get user ID from stored token to request a new one
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return null;
      }

      // Decode token to get user ID
      const base64Url = token.split('.')[1];
      const base64 = base64Url.replace(/-/g, '+').replace(/_/g, '/');
      const jsonPayload = decodeURIComponent(atob(base64).split('').map(function(c) {
        return '%' + ('00' + c.charCodeAt(0).toString(16)).slice(-2);
      }).join(''));
      
      const decodedToken = JSON.parse(jsonPayload);
      const userId = decodedToken.id;

      // Request new token
      const response = await fetch(`${config.apiUrl}/api/users/refresh-token`, {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ userId })
      });

      if (!response.ok) {
        // If token refresh fails, redirect to login
        navigate('/auth');
        return null;
      }

      const data = await response.json();
      
      // Store new token
      localStorage.setItem('token', data.token);
      
      // Return the new token
      return data.token;
    } catch (error) {
      console.error('Token refresh failed:', error);
      navigate('/auth');
      return null;
    }
  };

  useEffect(() => {
    // Fetch user info and stats from backend API
    async function fetchDashboardData() {
      const token = localStorage.getItem('token');
      if (!token) {
        navigate('/auth');
        return;
      }
      try {
        console.log('Fetching dashboard data...');
        let token = localStorage.getItem('token');
        
        // First fetch user profile to get avatar
        let userResponse = await fetch(`${config.apiUrl}/api/users/profile`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        
        // Handle token expiration for user profile request
        if (userResponse.status === 401) {
          const responseData = await userResponse.json();
          
          if (responseData.expired) {
            // Try to refresh token
            const newToken = await refreshToken();
            if (!newToken) return;
            
            token = newToken;
            
            // Retry with new token
            userResponse = await fetch(`${config.apiUrl}/api/users/profile`, {
              headers: {
                'Authorization': `Bearer ${newToken}`,
              },
            });
          }
        }
        
        if (userResponse.ok) {
          const userData = await userResponse.json();
          if (userData.profile_dashboard) {
            setUserAvatar(`${config.apiUrl}/uploads/${userData.profile_dashboard}`);
          } else if (userData.profile_thumbnail) {
            setUserAvatar(`${config.apiUrl}/uploads/${userData.profile_thumbnail}`);
          } else if (userData.profile_image) {
            setUserAvatar(`${config.apiUrl}/uploads/${userData.profile_image}`);
          }
        }
        
        // Then fetch dashboard data
        let response = await fetch(`${config.apiUrl}/api/dashboard`, {
          headers: {
            'Authorization': `Bearer ${token}`,
          },
        });
        
        // Handle token expiration for dashboard request
        if (response.status === 401) {
          const responseData = await response.json();
          
          if (responseData.expired) {
            // Try to refresh token if not already refreshed
            if (token === localStorage.getItem('token')) {
              const newToken = await refreshToken();
              if (!newToken) return;
              
              token = newToken;
            }
            
            // Retry with new token
            response = await fetch(`${config.apiUrl}/api/dashboard`, {
              headers: {
                'Authorization': `Bearer ${token}`,
              },
            });
          }
        }
        
        console.log('Response status:', response.status);
        if (response.ok) {
          const data = await response.json();
          console.log('Dashboard data received:', data);
          setUserRole(data.role);
          setUserName(data.name);
          setDisplayName(data.displayName || `${data.role.charAt(0).toUpperCase() + data.role.slice(1)} User`);
          setStats({
            properties: data.propertiesCount,
            regions: data.regionsCount,
            agencies: data.agenciesCount,
            leads: data.leadsCount,
          });
          setRecentLeads(data.recentLeads);
        } else {
          console.error('Dashboard fetch failed with status:', response.status);
          // If unauthorized, redirect to auth page
          navigate('/auth');
        }
      } catch (error) {
        console.error('Failed to fetch dashboard data:', error);
        navigate('/auth');
      }
    }
    fetchDashboardData();
  }, [navigate]);

  // Role-based access control helpers
  const canManageListings = userRole === 'admin' || userRole === 'agency' || userRole === 'client';
  const canManageContent = userRole === 'admin';
  const canManageUsers = userRole === 'admin' || userRole === 'agency';

  // Handle sign out
  const handleSignOut = () => {
    // Remove the token from localStorage
    localStorage.removeItem('token');
    // Redirect to the login page
    navigate('/auth');
  };

  const handleMyProfile = () => {
    navigate('/profile');
  };

  return (
    <div className="dashboard-container">
      <aside className="sidebar">
        <div className="user-info">
          {userAvatar ? (
            <div className="avatar">
              <img src={userAvatar} alt={userName || 'User'} />
            </div>
          ) : (
            <div className="avatar">{userName ? userName.charAt(0).toUpperCase() : 'U'}</div>
          )}
          <div className="user-details">
            <div className="user-name">{userName || 'User'}</div>
            <div className="user-role">{displayName || (userRole ? userRole.charAt(0).toUpperCase() + userRole.slice(1) : '')}</div>
          </div>
        </div>
        
        <div className="sidebar-content">
          <nav className="nav-links">
            <button className="nav-link active">Dashboard</button>
            {canManageListings && <button className="nav-link">Properties</button>}
            <button className="nav-link">Regions</button>
            {userRole === 'admin' && <button className="nav-link">Agencies</button>}
            {canManageListings && <button className="nav-link">Leads</button>}
            {canManageUsers && (
              <button 
                className="nav-link"
                onClick={() => navigate('/dashboard/users')}
              >
                {userRole === 'admin' ? 'User Management' : 'My Clients'}
              </button>
            )}
            <button 
              className="nav-link"
              onClick={handleMyProfile}
            >
              My Profile
            </button>
            <button className="nav-link">Settings</button>
          </nav>
          
          <div className="sidebar-footer">
            <button className="nav-link sign-out" onClick={handleSignOut}>
              Sign Out
            </button>
          </div>
        </div>
      </aside>
      
      <main className="main-content">
        <h1>Dashboard</h1>
        <p>Welcome back, {userName || 'User'}</p>
        <div className="stats-cards">
          <div className="card">
            <div className="card-icon">üè†</div>
            <div className="card-info">
              <div className="card-count">{stats.properties}</div>
              <div className="card-label">Properties</div>
            </div>
          </div>
          <div className="card">
            <div className="card-icon">üìç</div>
            <div className="card-info">
              <div className="card-count">{stats.regions}</div>
              <div className="card-label">Regions</div>
            </div>
          </div>
          {userRole === 'admin' && (
            <div className="card">
              <div className="card-icon">üè¢</div>
              <div className="card-info">
                <div className="card-count">{stats.agencies}</div>
                <div className="card-label">Agencies</div>
              </div>
            </div>
          )}
          <div className="card">
            <div className="card-icon">‚úâÔ∏è</div>
            <div className="card-info">
              <div className="card-count">{stats.leads}</div>
              <div className="card-label">Leads</div>
            </div>
          </div>
        </div>
        <section className="recent-leads">
          <h2>Recent Leads</h2>
          {recentLeads.length === 0 ? (
            <p>No recent leads found.</p>
          ) : (
            <ul>
              {recentLeads.map((lead) => (
                <li key={lead.id}>
                  {lead.name} - {lead.email} - {lead.phone}
                </li>
              ))}
            </ul>
          )}
        </section>
      </main>
    </div>
  );
};

export default Dashboard;
